import { View, Text,Button } from "react-native"
import {
    getHash,
    startOtpListener,
    useOtpVerify,
  } from 'react-native-otp-verify';
export default function Home(props) {
    const { hash, otp, message, timeoutError, stopListener, startListener } = useOtpVerify({numberOfDigits: 4});
    useEffect(() => {
        getHash().then(hash => {
          // use this hash in the message.
        }).catch(console.log);
      
        startOtpListener(message => {
          // extract the otp using regex e.g. the below regex extracts 4 digit otp from message
          const otp = /(\d{4})/g.exec(message)[1];
          setOtp(otp);
        });
        return () => removeListener();
      }, []);
    return (
        <View style={{flex:1,justifyContent:"center",alignItems:"center"}}>
            <Text>Home</Text>
            <Button title="products" onPress={()=>{props.navigation.navigate("products")}}/>
            <Button title="insta" onPress={()=>props.navigation.navigate("insta")}/>
        </View>
    )
}
