import {ScrollView,View,StyleSheet,Text,Button,Image,Platform,Dimensions} from "react-native"
import { Feather, AntDesign } from '@expo/vector-icons';
export default function Insta(){
    return(<>
    
        <ScrollView style={styles.post}>
            <View style={{display:"flex",alignItems:"flex-start"}}>
            <Text style={styles.title}>Ind vs WI</Text>
            <View style={Platform.OS.toLowerCase()==="web"?styles.cardWeb:styles.cardMobile}>
                <Image style={Platform.OS.toLowerCase()=="web"?{width:"100%",height:"100%",borderRadius:"10px"}:{width:Dimensions.get("window").width-20,height:Dimensions.get("window").height,borderRadius:10}} source={require("../assets/post.jpg")}/>
            </View>
            <View style={styles.bottomIcons}>
                <Button style={{backgroundColor:"white"}} title={<AntDesign name="hearto" size={24} color="black" />} onPress={()=>{
                    alert('send')
                }} />                
                <Button title={<Feather name="send" size={24} color="black"/>} onPress={()=>{
                    alert('send')
                }} />
            </View>
            </View>
        </ScrollView>
    </>)
}

const styles=StyleSheet.create({
    title:{
        fontWeight:"bold"
    },
    post:{
        display:"flex",
        border:"1px solid red",

    },
    cardMobile:{
        border:"1px solid #0005",
        paddingTop:10,  
        paddingRight:10,
        paddingLeft:10,
        width:"100%",
        marginTop:10,
    },
    cardWeb:{
        border:"1px solid #0005",
        marginTop:"10px",
        borderRadius:"10px",      
        paddingTop:"10px",
        paddingBottom:"10px",
        paddingLeft:"5px",
        paddingRight:"5px",  
        width:300,
        height:300,
    },
    bottomIcons:{
        width:"100%",
        display:"flex",
        flexDirection:"row",
        alignItems:'center',
        justifyContent:'space-between'
    }            
    
})
